E=C+(D-A);
F=C+(B-A);
G=D+(B-A);

draw(A--D--E--C--A--B--G--D);
draw(C--F--B);

a=(3/4)*F+(1/4)*B;
b=(1/2)*F+(1/2)*B;
c=(1/4)*F+(3/4)*B;

m=(3/4)*C+(1/4)*A;
n=(1/2)*C+(1/2)*A;
o=(1/4)*C+(3/4)*A;

j=(3/4)*E+(1/4)*D;
